mapper.select:


    and cus_code = #{cusCode}
    and order_no like concat('%', #{orderNo}, '%')
    and status = #{status}

        and date_format(entry_time,'%y%m%d') >= date_format(#{params.beginEntryTime},'%y%m%d')


        and date_format(entry_time,'%y%m%d') <= date_format(#{params.endEntryTime},'%y%m%d')


order by create_time desc

SELECT
  order_qty
from (
    SELECT
        cus_code,bom_id,order_type,sum(order_qty) as order_qty
    FROM
        `smt_order_entry` e
        LEFT JOIN `smt_order_entry_line` el ON e.id = el.entry_id 	AND el.del_flag = 0
        WHERE e.del_flag = 0
        group by cus_code,bom_id,order_type
    ) a
    where a.cus_code = #{cusCode}
    and a.order_type = #{orderType} and a.bom_id = #{bomId}

mapper.update:
update smt_order_entry
cus_code = #{cusCode}, order_no = #{orderNo}, entry_time = #{entryTime}, status = #{status}, create_by = #{createBy}, create_time = #{createTime}, update_by = #{updateBy}, update_time = #{updateTime}, remark = #{remark}, del_flag = #{delFlag},
where id = #{id}
